import React from "react";
import { Component } from "react";
import "../index2.scss"
import history from "../index"

export default class layout extends Component {
  constructor(props) {
    super(props);

    const currentTab = document.location.pathname;
    console.log('currentTab? ', currentTab);

    this.state = {
      clickedTab: currentTab.substr(1),
      // isClicked: false
    };
  };

  // this.handlePasswordChange = this.handlePasswordChange.bind(this);
  // this.toggleShow = this.toggleShow.bind(this);


  goToAbout = () => {
    this.setState({ clickedTab: 'about' });
    history.push("/about");
  }

  onTabClick = (clickedTab) => {
    this.setState({ clickedTab: clickedTab });
    history.push(`/${clickedTab}`);
  }

  isTabClicked = (currentTab) => currentTab === this.state.clickedTab;

  // checkbox(){
  //   if(this.state.isClicked){
  //     document.querySelector("project-item").classList.remove("cross");

  //     this.state.isClicked = false;
  //   }
  //   else{
  //     document.querySelector("project-item").classList.add("cross");
  //     this.state.isClicked = true;
  //   }
  // } 
  // const y= '123123'
  // const x = `test${y}`;
  // const x = 4 + 'asdasd ' + 'asdasd'

  render() {
    const { clickedTab } = this.state;
    return (
      <div className="projectsPageContainer">
        <div className="sideMenu">
          <div className="top">
            <div className="menuIcon"></div>
            <div className="menu-home-container">
              <div className={`menuHome ${this.isTabClicked('home') ? 'clicked' : ''}`} />
              <div className="sideMenuText">Home</div>
            </div>
            <div className={`menu-about-container ${this.isTabClicked('about') ? 'clicked' : ''}`} onClick={() => this.onTabClick('about')}>
              <div className='menuAbout' />
              <div className="sideMenuText">About</div>
            </div>
            <div className="menu-settings-container">
              <div className="menuSettings"></div>
              <div className="sideMenuText">Settings</div>
            </div>
          </div>
          <div className="bottomSideMenu">
            <div className="menuLogout"></div>
            <div className="sideMenuTextLogout">Logout</div>
          </div>
        </div>
        <div className="mainMenu">
          <div className="main-header">
            <div className="logo"></div>
            <div className="user">user@spectory.com</div>
          </div>
          <div className="lists">
            {this.props.children}
          </div>
        </div>
      </div>
    )
  }
}
