import React from "react";
import {Component} from "react";
// import "./layout.scss"
import "./projectsList.scss"
import history from "../index"
import Layout from './layout'
import ProjectsComponent from "./projects"
import TasksComponent from "./tasks"

class ProjectsListComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      projects: [],
      chosenProjectIndex: -1,
      // tasks: [],
    };

    // this.handlePasswordChange = this.handlePasswordChange.bind(this);
    // this.toggleShow = this.toggleShow.bind(this);
  }


addProject(){
  // console.log("Anastasia");
  // var newProject = new ProjectsComponent();
  const updatedProjectsArray = [...this.state.projects, {name: "Add New Project name", tasks: []}];
  const chosenProjectIndex = updatedProjectsArray.length -1;
  // this.setState({projects: [...this.state.projects, {name: "Add New Project name", tasks: []}], chosenProject: });
  this.setState({projects: updatedProjectsArray, chosenProjectIndex});
}

addTask(){
  const {projects, chosenProjectIndex} = this.state;
  const updatedProjectsArray = [...projects];
  updatedProjectsArray[chosenProjectIndex].tasks.push({name: "Add New Task description"});

  // this.setState({tasks: [...this.state.tasks, {name: "Add New Task description"}]});
  this.setState({projects: updatedProjectsArray});
}

// onSubmit = () => {
//   history.push("/");
// }

  // checkbox(){
  //   if(this.state.isClicked){
  //     document.querySelector("project-item").classList.remove("cross");

  //     this.state.isClicked = false;
  //   }
  //   else{
  //     document.querySelector("project-item").classList.add("cross");
  //     this.state.isClicked = true;
  //   }
  // } 

  handleProjectClick = (i) => {
    this.setState({chosenProjectIndex: i});
  }

  render() {
    const {projects, chosenProjectIndex} = this.state;
    return (
      <Layout>
          <div className="projectsList">
            <div className="projects-header">
              <div className="my-projects-header-text">My Projects</div>
              <div className="new-project-header">
                <div className="new-project-header-text">New Project</div>
                <div className="new-project-header-icon" onClick={() => this.addProject()}></div>
                </div>
            </div>
            <div className="project-items-container">
            {/* {this.state.projects.map((p, i) => <ProjectsComponent name={p.name} handleClick={() => this.handleProjectClick()} />)} */}
            {projects.map((p, i) => <div className="project-item-parent" onClick={this.handleProjectClick}> <ProjectsComponent key={`${p.name}_${i}`} name={p.name} handleClick={this.handleProjectClick} /> </div>)}

              {/* <div className={`menu-home-container ${display} ${this.isTabClicked('projects') ? 'clicked' : ''}`} onClick={() => this.onTabClick('projects')}> */}


    {/* {projects.map((p, i) => <div className={`project-item-parent ${chosenProjectIndex==i ? 'clicked' : ''}`} onClick={this.handleProjectClick}> <ProjectsComponent key={`${p.name}_${i}`} name={p.name} handleClick={this.handleProjectClick} /> </div>)} */}


            </div>
          </div>
          <div className="tasksList">
            <div className="tasks-header">
              <div className="current-project">Project name</div>
              <div className="new-task-header">
                <div className="new-task-header-text">New Task</div>
                <div className="new-task-header-icon" onClick={() => this.addTask()}></div>
              </div>
              </div>
                 <div className="task-items-container">
                  {chosenProjectIndex !== -1 && projects[chosenProjectIndex].tasks.map((t, j) => <TasksComponent key={`${t.name}_${j}`} name={t.name} />)}


            </div>
          </div>
      </Layout>
    );
  }
}

export default ProjectsListComponent;
