import React from "react";
import {Component} from "react";
import Layout from './layout'
import "./settings.scss"
// import "../js/actions/index"
// import "../js/constants/action-types"
import * as actions from '../js/actions';
import { connect } from 'react-redux';

class Settings extends Component {
    constructor(props) {
      super(props);

      // this.state = {
      //   isClicked: false
    };

      // this.handlePasswordChange = this.handlePasswordChange.bind(this);
      // this.toggleShow = this.toggleShow.bind(this);


  // onSubmit = () => {
  //   history.push("/");
  // }

    // checkbox(){
    //   if(this.state.isClicked){
    //     document.querySelector("project-item").classList.remove("cross");

    //     this.state.isClicked = false;
    //   }
    //   else{
    //     document.querySelector("project-item").classList.add("cross");
    //     this.state.isClicked = true;
    //   }
    // } 

    render() {
      console.log('this props settings ? ', this.props);
      if(this.props.display === "Turquoise"){
        document.getElementsByClassName("settings-header-options-cancel")[0].classList.add("Turquoise");
      }
      const { display } = this.props;
      return (
        <Layout>
          <div className="settings-layout">
              <div className="settings-header-container">
                  <div className="settings-header-title">Themes</div>
                  <div className="settings-header-options">
                        <div className={`settings-header-options-cancel ${display}`}>Cancel Turquoise</div>
                      <div className="settings-header-options-save">Save</div>
                  </div>
              </div>
              <div className="pick-theme-text">Pick your theme</div>
              <div className="themes">
                  <div className="dark-theme">
                      <div className="dark-theme-icon" onClick = {() => this.props.switchDisplay("Dark")}/>
                      <div className="dark-theme-text">Dark (Default)</div>
                  </div>
                  <div className="turquoise-theme">
                      <div className="turquoise-theme-icon" onClick = {() => this.props.switchDisplay("Turquoise")}/>
                      <div className="turquoise-theme-text">Turquoise</div>
                  </div>
              </div>
          </div>
        </Layout>
          )
      }
  }

  function mapStateToProps(state) {
    console.log('state ? ', state);
    return { 
      display: state.display
      // if(display: state.display === "Dark"){

      // }
      // else{

      // }
    }
  }

  function mapDispatchToProps(dispatch) {
    return {
      switchDisplay: display => dispatch(actions.switchDisplay(display)),

    }
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Settings);
